object StringExt {
    // Provide an implicit class, which will work as an implicit conversion
    implicit class StringHolder(str:String) {
        def funny= str.map(x => if (scala.util.Random.nextBoolean()) x.toUpper else x.toLower)
    }
}

class FunnyStringHolder(str:String) {
    def superFunny = str.map(x => if (scala.util.Random.nextBoolean()) x.toUpper else x.toLower).reverse
}

object StringOps {
    implicit def toFunnyStringHolder(str:String) = new FunnyStringHolder(str)
}
