class JDBCSink(url: String, user:String, pwd:String) extends org.apache.spark.sql.ForeachWriter[org.apache.spark.sql.Row] {
    import org.apache.commons.lang.StringEscapeUtils

    val driver = "com.mysql.cj.jdbc.Driver"

    @transient var connection:java.sql.Connection = _
    @transient var statement:java.sql.Statement = _

    def open(partitionId: Long, version: Long):Boolean = {
        Class.forName(driver)
        connection = java.sql.DriverManager.getConnection(url, user, pwd)
        statement = connection.createStatement
        true
    }

    def process(value: org.apache.spark.sql.Row): Unit = { 
        try {
            val ts = value(0).toString
            val topic = StringEscapeUtils.escapeSql(value.getString(1))
            val freq = value.getLong(2)            
            statement.executeUpdate(s"INSERT INTO twitter_topics(ts, topic, frequency) VALUES ('$ts','$topic',$freq) ON DUPLICATE KEY UPDATE frequency=$freq;")
        }
        catch {
            case t:Throwable =>
        }
    }

    def close(errorOrNull:Throwable):Unit = {
        connection.close
        connection = null
        statement = null
    }
}
